CREATE TABLE bank_account (     id integer primary key,     currency text not null,     iban text not null,     name text not null );
CREATE TABLE statement_line (     id integer primary key,     account integer not null references bank_account(id),      effective_date date not null,    amount_in_cents integer not null,     memo text,     line_type text,     bank_id text,     name text,     matched_in_accounting boolean not null default false,     UNIQUE(account, bank_id) );
